    public static ArrayList<Candi> getListData() {
        Candi candi;
        ArrayList<Candi> list = new ArrayList<>();

        for (String[] mData : data) {
            candi = new Candi();
            candi.setNama(mData[0]);
            candi.setGambar(mData[1]);
            candi.setKeterangan(mData[2]);
            candi.setLatitude(mData[3]);
            candi.setLongitude(mData[4]);

            list.add(candi);
        }

        return list;
    }

